import React , {Component, Fragment} from 'react';
import {BrowserRouter as Router, Link, Switch, Route} from 'react-router-dom';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Container from 'react-bootstrap/Container';
import Image from 'react-bootstrap/Image';
import Logo from './../assets/logo.png';
import FilmsPage from './FilmsPage';
import HomePage from './HomePage';
import PeoplePage from './PeoplePage';

class StudioGhibliWiki extends Component {


    componentDidMount() {
    window.addEventListener('load', (e) => {
        alert(window.location.href);
        /*
      let myClass = "HomeCl";
     let classElement = document.getElementsByClassName(myClass)[1];
      let mainElement = document.getElementsByClassName(myClass)[0];
      classElement.click();
     mainElement.click();*/
});
    }
    render(){
         let addClose = (mainelement, span, x )  =>{

                mainelement.style.marginLeft= "auto";
     mainelement.style.border = "transparent";
               mainelement.removeChild(mainelement.firstElementChild);
                span.appendChild(x);
                span.classList.add("text-white", "close");
                mainelement.appendChild(span);
                return mainelement;
 } ;
        return(
            <React.Fragment>
                 <Router>
            <Fragment>
<Navbar  className="py-3" collapseOnSelect  expand="md" bg="dark" variant="dark" fixed="top">


    <Row className="w-25"> 
    <Navbar.Brand className="ml-3 mr-auto " >
        <Col> <Image className=" bg-white" src={Logo} height={"50"} width={"160"} rounded /></Col><Col className="mt-2">Studio Ghibli Wiki </Col></Navbar.Brand>
    </Row>
    <Navbar.Toggle className="px-4 py-3 "   onClick={(e) => {
                let elem = document.getElementsByClassName("navbar-toggler")[1];
                let x = document.createTextNode("×");
                let span = document.createElement("span");
                addClose(elem, span, x);
                }
                           }aria-controls="responsive-navbar-nav" />

     <Navbar.Collapse className="mt-auto" id="responsive-navbar-nav">
        <Nav id="mainnav" className="ml-auto w-75 d-none d-md-flex justify-content-md-between" >
            <Nav.Link as={Link} exact  to="/"  className="HomeCl" onClick={(e)=>{ document.getElementsByClassName("HomeCl")[1].click();}}  
                href="Home">Home</Nav.Link>

            <Nav.Link as={Link} to="/films" className="AboutCl" onClick={(e)=>{ document.getElementsByClassName("AboutCl")[1].click();}} 
                href="About">View Films</Nav.Link>

            <Nav.Link as={Link} to="/people"  className="AddCl" onClick={(e)=>{ document.getElementsByClassName("AddCl")[1].click();}}  
                href="Add">View People</Nav.Link>
        </Nav>

        <Nav id="nav" className="mx-auto w-100 d-md-none text-center mx-5 my-3  border border-white rounded mb-2" >
        <Navbar.Toggle />
            <Nav.Link as={Link} exact to="/" className=" mb-2 mt-auto HomeCl  border border-white mx-3" href="Home"  onClick={(e)=>{ document.getElementsByClassName("HomeCl")[0].click();}}  

                >Home</Nav.Link>



            <Nav.Link as={Link} to="/films" className=" mb-2 AboutCl border border-white mx-3" href="About"  
            onClick={(e)=>{ document.getElementsByClassName("AboutCl")[0].click();}} 
                >View Films</Nav.Link>


             <Nav.Link as={Link} to="/people"   className="mb-2 AddCl border border-white mx-3 mb-4" href="Add" onClick={(e)=>{ document.getElementsByClassName("AddCl")[0].click();}}    

               >View People</Nav.Link>
            </Nav>

    </Navbar.Collapse>

</Navbar> 
    <Container className="p-5" fluid>
        <Row className="mt-5"></Row>
        <Row className="mt-5">
                <Switch>
                        <Route exact path="/" component={HomePage} />
                        <Route path="/films" component={FilmsPage}/>
                     <Route path="/people" component={PeoplePage}/>
                    </Switch>
                </Row>
                </Container>
             </Fragment>
         </Router>
            </React.Fragment>

        );
    }
}
export default StudioGhibliWiki;
